CREATE TABLE `Questions` ( `Id` INT NOT NULL AUTO_INCREMENT , `Submit` DATETIME NOT NULL , `Title` VARCHAR(300) NOT NULL , `Choice1` VARCHAR(3000) NOT NULL , `Choice2` VARCHAR(3000) NOT NULL , `Choice3` VARCHAR(3000) NOT NULL , `Choice4` VARCHAR(3000) NOT NULL , PRIMARY KEY (`Id`)) ENGINE = InnoDB;
ALTER TABLE `Answers` ADD `QuestionId` INT NOT NULL AFTER `Choice`;